const INITIAL_VALUE={
    setCurrentUser:null
} 
 const userReducer=(state=INITIAL_VALUE, action)=>{
     switch(action.type){
         case 'SET_CURRENT_USER':
             return{
                 setCurrentUser:action.payload
             }
             default:
              return state;
     }
 }
 
 export default userReducer;
