const INITIAL_VALUE={
    currentUser:null
}
const userReducer=(state= INITIAL_VALUE ,action)=>{
switch(action.type){
    case "SET_CURRENT_USER":
    return{
    currentUser:action.payload
    }
    default:return state;

}

}
export default userReducer;
