import React from 'react';

import'./checkout-item.style.scss'

import{addItem, clearItemFromCart} from'../../redux/cart/cart.action'

import {createStructuredSelector}from 'reselect'

import {connect} from 'react-redux'

const CheckoutItem =({cartItems:{name, price ,imageUrl,quantity} ,clearItem})=>{
    console.log()
    return(

    <div className='checkout-item'>
        <div className='image-container' >
            <img className=' img' src={imageUrl} alt={name}/>
        </div>
        <span className='name'> {name}</span>
        <span className='quantity'> {quantity}</span>
        <span className='price'> ${price}</span>
        <div onClick={()=>{clearItem()}}className='remove-button'> &#10005;</div>
    </div>
)}
// const mapStateToProps=createStructuredSelector({
//     clearItem:clearItemFromCart
// })

const mapDispatchToProps=dispatch=>({
    clearItem:item=>dispatch(clearItemFromCart(item))
})
export default connect(mapStateToProps) (CheckoutItem);
