import React from 'react';
import './sign-in.style.scss';

const handleSubmit = (event)=>{
    event.preventDefault()
    this.setState({email:'', password:''})
}

 const handelChange= (event)=>{
    const {value,name}=event.target;
    this.setState({[name]:value})

}
class SignIn extends React.Component{
    constructor(props){
        super(props);
    this.state={email:'',
    password:''}
    }
     handleSubmit = (event)=>{
        event.preventDefault()
        this.setState({email:'', password:''})
    }
    
      handelChange= (event)=>{
        const {value,name}=event.target;
        this.setState({[name]:value})
    
    }

    render(){
        return(
            <div className='sign-in'>
<h2>I Have Already An ACcount</h2>
<span>Sign In With Your Email And Password</span>
<form onSubmit={handelChange}>
    <input type='email'name='email' onChange={handelChange} value={this.state.email}  required/>
    <label> Email</label>

    <input type='password'name='password' onSubmit={this.handelChange}value={this.state.password} required/>
    <label> Password</label>
    <submit type='submit'value='Submit Form' onSubmit={this.handleSubmit}> Submit</submit>
</form>
            </div>
        )
    }

}

export default SignIn;
