import { createSelector } from "reselect";

const selectCartItems = (state) => state.cart;
export const selectCart = createSelector(
  [selectCartItems],
  (cart) => cart.cartItems
);

export const selectCartItemCount = createSelector([selectCart], (cartItems) =>
  cartItems.reduce(
    (accumilatedQuantity, cartItems) =>
      accumilatedQuantity + cartItems.quantity,
    0
  )
);
