import React from 'react';
import {Route} from 'react-router-dom'
// import {withRouter} from 'react-router-dom'
import CollectionOverview from '../../components/collection-overview/collection-overview.component'
import CollectionPage from'../collection/collection.component'
import {firestore , convertCollectionsSnapshotToMap} from '../../firebase/firebase.utli'
import {connect} from 'react-redux'
import  {updateCollections} from '../../redux/shop/shop.action'
import WithSpinner from '../../components/with-spinner/with-spinner.component'


const CollectionPagewWithSpinner=(prams)=>{WithSpinner(CollectionPage)}

const CollectionOverviewWithSpinner=(prams)=>{WithSpinner(CollectionOverview)}
class ShopComponent extends React.Component{


unsubuscribeFromSnapshot=null
state={
  loding:true
}

componentDidMount(){
  const {updateCollections}=this.props
  const collectionRef=firestore.collection('collections');
  this.unsubuscribeFromSnapshot= collectionRef.onSnapshot(async snapshot=>{const collectionMap= convertCollectionsSnapshotToMap(snapshot)
  updateCollections(collectionMap);
this.setState({loading:false})})
}

render(){
  const {match }=this.props
  const {loading}=this.state
   return(
            <div className='shop-page'>
              <Route exact path={`${match.path}`} render={(props)=><CollectionOverviewWithSpinner isLoding={loading} {...props}/>}/>
              <Route exact path={`${match.path}/:collectionId`} render={ props=><CollectionPagewWithSpinner isLoding={loading} {...props}/>}/>
      
            </div>
        )}};        
  const mapDispatchToProps=dispatch=>({
    updateCollections :collectionMap=>dispatch( updateCollections(collectionMap))
  })      
export default connect(null,mapDispatchToProps)(ShopComponent);
