import {connect} from 'react-redux';
import {createStructuredSelector} from 'reselect';
import WithSpinner from '../with-spinner/with-spinner.component';
import CollectionPreview from './collection-preview.component';
import {compose} from 'redux';
import {selectIsCollectionsLoaded} from '../../redux/shop/shop.selector';
import collectionPreviewComponent from './collection-preview.component';

const mapStateToProps=createStructuredSelector({
isLoading:!selectIsCollectionsLoaded
});

const CollectionPreviewContainer=compose(connect(mapStateToProps),(WithSpinner))(collectionPreviewComponent) ;

export default CollectionPreviewContainer;
